package interview.program;

public class SwapTwonumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		System.out.println("Before Swaping:a="+ a+", b="+b);
		a=a+b;   //a=30
		b=a-b;   //b=10
		a=a-b;   //a=20
		
		
//		int temp=a; //temp=10
//		a=b;// a=20
//		b=temp; //b=10
		System.out.println("After Swaping:a="+a+",  b="+b);
	

	}

}
